def.body:
@matchers.each do |m|
  if m.match(p)
  else
    return nil
  end
end
@cb

@xmlns = node.namespace.try(&.href) || ""
node.attributes.each do |attr|
  case attr.name
  when "type"
    @type = attr.children[0].content
  when "id"
    @id = attr.children[0].content
  when "from"
    @from = attr.children[0].content
  when "to"
    @to = attr.children[0].content
  when "lang"
    @lang = attr.children[0].content
  else
  end
end

cls = new()
node.children.select(&.element?).each do |child|
  ns = (child.namespace.try(&.href)) || ""
  case {child.name, ns}
  when {Caps.xml_name.local, Caps.xml_name.space}
    cls.caps = Caps.new(child)
  when {TLSStartTLS.xml_name.local, TLSStartTLS.xml_name.space}
    cls.start_tls = TLSStartTLS.new(child)
  when {SASLMechanisms.xml_name.local, SASLMechanisms.xml_name.space}
    cls.mechanisms = SASLMechanisms.new(child)
  when {Bind.xml_name.local, Bind.xml_name.space}
    cls.bind = Bind.new(child)
  when {StreamManagement.xml_name.local, StreamManagement.xml_name.space}
    cls.stream_management = StreamManagement.new(child)
  when {StreamSession.xml_name.local, StreamSession.xml_name.space}
    cls.session = StreamSession.new(child)
  when {P1Push.xml_name.local, P1Push.xml_name.space}
    cls.p1_push = P1Push.new(child)
  when {P1Rebind.xml_name.local, P1Rebind.xml_name.space}
    cls.p1_rebind = P1Rebind.new(child)
  when {P1Ack.xml_name.local, P1Ack.xml_name.space}
    cls.p1_ack = P1Ack.new(child)
  else
    cls.any << (Node.new(child))
  end
end
cls

if (t = start_tls)
  return {t, true}
end
{TLSStartTLS.new, false}

@client.event_handler = ->event_handler(Event)
@wg.add(1)
begin
  connect
rescue exception
  @wg.done
  raise(exception.message || exception)
end
@wg.wait
